mapper.select:
INSERT INTO CAFE_RE
(
      CR_NO
    , CR_CONTENT
    , CR_GRADE
    , CR_REG_DATE
    , CR_DEL, CR_DEL_DATE
    , USER_ID
    , C_NO
)
VALUES(
      #{cr_no}
    , #{cr_content}
    , #{cr_grade}
    , SYSDATE
    , 'N', SYSDATE
    , #{user_id}
    , #{c_no}
)

mapper.update:
UPDATE CAFE_RE
   SET CR_CONTENT = #{cr_content}
     , CR_GRADE = #{cr_grade}
 WHERE CR_NO = #{cr_no}
   AND C_NO = #{c_no}

UPDATE CAFE_RE
   SET CR_DEL      = 'Y'
     , CR_DEL_DATE = SYSDATE
 WHERE CR_NO = #{cr_no}
   AND C_NO = #{c_no}
